import React, { Component } from 'react';
import { connect } from 'react-redux'

class CreateSpotify extends Component{

   state = {
        url: null,
        playName: null,
    }
    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
        console.log(this.state.playName)

    }
    handleSubmit = async (e) => {
        e.preventDefault();
        const { spotifyToken } = this.props;
        const Token = { spotifyToken };
        var tag = this.state.playName
        const { idA2S } = this.props;
        const id = { idA2S };
        fetch('http://localhost:8888/spotifyplaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'createplaylist': tag,
                    'authToken': Token,
                    'id':idA2S
                })
        })
    }

    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Name of your new Spotify playlist</label>
            <input type = "text" id="playName" onChange={this.handleChange}></input>
            <button>Submit</button>

            </form>

        </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        spotifyToken: state.spotifyToken,
        idA2S: state.idA2S
    }
}

export default connect(mapStateToProps)(CreateSpotify);
