   }
    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Spotify playlist URL</label>
            <input type = "text" id="url" onChange={this.handleChange}></input>
            <button>Submit</button>
            </form>

        </div>

        )
    }
}

export default Spotify;
