import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Form, Button } from 'react-bootstrap';

class Converter extends Component{


    state = {
        url: null,
        playName: null,
        link: null,
        done: null
    }

    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
        console.log(this.state)

    }

    handleSubmit = (e) => { 


        e.preventDefault();

        this.setState({
            done:"Converting...",
            link: null
        })


        const { source } = this.props;
        const theSource = { source };
        if ( theSource.source === "appleToSpotify" ){

            const { appleToken } = this.props;
            const { idA2S }=this.props
            const theid = { idA2S }
            const { spotifyToken } = this.props;
            let playName = this.state.playName
            let playlistURI = this.state.url



            let getAppleISRC = async function(){
                fetch('http://localhost:8888/getAppleISRC',{
                    method: 'POST',
                    headers: {
                        'Content-type': 'application/json'
                },
                    body:JSON.stringify({
                        'url': playlistURI,
                        'authToken': { appleToken },
                        'id': theid
                })

                })/*.then((response)=>{
                    setTimeout(function(){
                        console.log(response.json())
                    },5000)
                })*/
            }

        let createSpotifyPlaylist =  (async()=>{
            fetch('http://localhost:8888/createSpotifyPlaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'createplaylist': playName,
                    'authToken': {spotifyToken},
                    'id':theid
                })
            }).then((response)=>{

                   return response.json()

            }).then((response)=>{

                    console.log(response)
                    console.log(response.done)
                    this.setState({
                        done:response.done,
                        link:response.link
                    })

            })
        })

        getAppleISRC().then(()=>{

            setTimeout(function(){
                createSpotifyPlaylist()

            },10000)
        })

        }
        if ( theSource.source ==="spotifyToApple"){

            const { appleToken } = this.props;
            const { spotifyToken } = this.props;
            const { id }=this.props
            const theid = { id }
            let playName = this.state.playName
            let playlistURI = this.state.url

            let getSpotifyISRC = async function(){

                fetch('http://localhost:8888/getSpotifyISRC',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'url': playlistURI,
                    'authToken': { spotifyToken },
                    'id': theid
                })

                })    
            }


        let createApplePlaylist = (async()=>{
            fetch('http://localhost:8888/createApplePlaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'playlist': playName,
                    'authToken': { appleToken },
                    'id': theid

                })
            }).then((response)=>{

                return response.json()

             }).then((response)=>{

                 console.log(response)
                 console.log(response.done)
                 this.setState({
                     done:response.done,
                     link:response.link
                 })

            })
        })

        getSpotifyISRC().then(()=>{
            setTimeout(function(){
                createApplePlaylist()
            },10000)
        })

        }

    }


    render(){

        return(
        <Form.Group>
          <div>
          <Form.Control size = "lg" type = "text" placeholder="Enter Playlist URL" />
          <Form.Control  size = "lg" type = "text" placeholder = "Enter Name of New Playlist"/>


            <Button variant="primary" type="submit" onClick={this.handleSubmit}>Convert!</Button>

            <h1>{this.state.done}</h1>
            <h1>{this.state.link}</h1>
          </div>

        </Form.Group>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        appleToken: state.appleToken,
        id: state.id,
        spotifyToken: state.spotifyToken,
        idA2S: state.idA2S,
        source: state.source
    }
}

export default connect(mapStateToProps)(Converter);
