import React, { Component } from 'react';
import { connect } from 'react-redux';

class Converter extends Component{
    constructor(props){
        super();

       this.state = {
            url: null,
            playName: null,
            link: null,
            done: null
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }





    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
        console.log(this.state)

    }
    handleSubmit = (e) => { 


        e.preventDefault();




        const { source } = this.props;
        const theSource = { source };
        if ( theSource.source === "appleToSpotify" ){

            const { appleToken } = this.props;
            const { idA2S }=this.props
            const theid = { idA2S }
            const { spotifyToken } = this.props;
            let playName = this.state.playName
            let playlistURI = this.state.url



            let getAppleISRC = async function(){
                fetch('http://localhost:8888/getAppleISRC',{
                    method: 'POST',
                    headers: {
                        'Content-type': 'application/json'
                },
                    body:JSON.stringify({
                        'url': playlistURI,
                        'authToken': { appleToken },
                        'id': theid
                })

                })/*.then((response)=>{
                    setTimeout(function(){
                        console.log(response.json())
                    },5000)
                })*/
            }

        let createSpotifyPlaylist = async function(){
            fetch('http://localhost:8888/createSpotifyPlaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'createplaylist': playName,
                    'authToken': {spotifyToken},
                    'id':theid
                })
            }).then((response)=>{

                   return response.json()

            }).then((response)=>{

                    console.log(response)
                    console.log(response.done)
                    this.setState({
                        done:response.done,
                        link:response.link
                    })

            })
        }

        getAppleISRC().then((response)=>{

            setTimeout(function(){
                createSpotifyPlaylist()

            },10000)
        })

        }
        if ( theSource.source ==="spotifyToApple"){

            const { appleToken } = this.props;
            const { spotifyToken } = this.props;
            const { id }=this.props
            const theid = { id }
            let playName = this.state.playName
            let playlistURI = this.state.url

            let getSpotifyISRC = async function(){

                fetch('http://localhost:8888/getSpotifyISRC',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'url': playlistURI,
                    'authToken': { spotifyToken },
                    'id': theid
                })

                })    
            }


        let createApplePlaylist = async function(){
            fetch('http://localhost:8888/createApplePlaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'playlist': playName,
                    'authToken': { appleToken },
                    'id': theid

                })
            })
        }

        getSpotifyISRC().then(()=>{
            setTimeout(function(){
                createApplePlaylist()
            },10000)
        }).then((response)=>{
            return response.json();
           })
         .catch((err)=>{
         console.log(err)
         }).then(response=>{
             console.log(response)
             this.setState({
                 done: response.done,
                 link: response.link
             })
         })

        }

    }


    render(){

        return(
        <div>
            <form onSubmit={this.handleSubmit} >
            <label for="playName" align="right">Enter Name of Your New Playlist</label>
            <label for="url" align="left">Enter URL of the Playlist You want to Convert</label>
            <br />
            <input type = "text" id="url" onChange={this.handleChange} align="left"></input>
            <input type = "text" id="playName" onChange={this.handleChange} align="right"></input>

            <button  >Convert!</button>
            </form>
            <h1>{this.state.done}</h1>
            <h1>{this.state.link}</h1>
        </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        appleToken: state.appleToken,
        id: state.id,
        spotifyToken: state.spotifyToken,
        idA2S: state.idA2S,
        source: state.source
    }
}

export default connect(mapStateToProps)(Converter);
