import React, { Component } from 'react';
import { connect } from 'react-redux'

class ApplePlaylist extends Component{
    state = {
        url: null,
    }
    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = async (e) => {
        e.preventDefault();
        var tag = this.state.url
        const { appleToken } = this.props;
        const Token = { appleToken };
        console.log(Token);
        const { id }=this.props
        const theid = { id }
        console.log(theid);
        fetch('http://localhost:8888/fillplaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'playlist': tag,
                    'authToken': Token,
                    'taskID': theid

                })
        })
    }
    render(){
        const { appleToken } = this.props;
        const Token = { appleToken };

        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Apple playlist URL</label>
            <input type = "text" id="url" onChange={this.handleChange}></input>
            <button>Submit</button>
            </form>

        </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        appleToken: state.appleToken,
        id: state.id
    }
}

export default connect(mapStateToProps)(ApplePlaylist);
