        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Spotify playlist URL</label>
            <input type = "text" id="url" onChange={this.handleChange}></input>
            <button>Submit</button>
            </form>

        </div>

        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return{
        addToken: (token) => {dispatch ({type: 'ADD_APPLETOKEN', payload: token})}
    }
}

export default connect(null, mapDispatchToProps)(Spotify);
