import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button } from 'react-bootstrap/';
import 'bootstrap/dist/css/bootstrap.min.css';

class AppleLogin extends Component {

    handleClickApple = async (e) => {
        let music = window.MusicKit.configure({
            developerToken:'eyJhbGciOiJFUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IjlSR0o1OUxIQVQifQ.eyJpYXQiOjE1OTcxODg5NTksImV4cCI6MTYxMjc0MDk1OSwiaXNzIjoiQkJEMzU1UDY3MyJ9.US0Y9NvwZqjy5IS0Ugcqzl0RJc9u8i4yEsrVjBt5f8bteLd0hLGLkuR4zpl-9AsUweif13kM3Ai58hicZiXhgA',
            app: {
                name: 'My Cool Web App'
            },
            declarativeMarkup: true
            });

        music.authorize().then(musicUserToken=>{
            console.log(musicUserToken);
            let token = musicUserToken;
            this.props.addToken(token);

            })


    }

    handleClickSpotify = async (e) => {
        const response = await fetch('http://localhost:8888/authorize',{
            method:'GET',
        });
        const data = await response.text();
        console.log(data);
        window.location = data;

    }

    render(){
        console.log(this.props)
        return(

        <div class = "text-center">
            <h1>Playlist Converter</h1>
            <h2>Convert your favorite playlists between music services!</h2>
            <br />
            <p>Choose Source and Destination</p>
           <Button class="mr-1" onClick={this.handleClickApple}> Login to Apple</Button>
           <div class="divider" />
           <Button onClick={this.handleClickSpotify}> Login to Spotify</Button>

        </div>

        )
    }
}



const mapDispatchToProps = (dispatch) => {
    return{
        addToken: (token) => {dispatch ({type: 'ADD_APPLETOKEN', payload: token})},

    }
}


export default connect(null, mapDispatchToProps)(AppleLogin);
