import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Form } from 'react-bootstrap';


class Source extends Component {

    handleSelect1 = (e) =>{
        var hash = document.URL
        hash = hash.substring(5).split("&")[0].split("=")[1];
        console.log(hash)
        this.props.addToken(hash)
        let source = "appleToSpotify";
        this.props.addSource(source);
        let idA2S = Math.floor(Math.random()*100000);
        this.props.addIDA2S(idA2S)


    }
    handleSelect2 = (e) =>{
        var hash = document.URL
        hash = hash.substring(5).split("&")[0].split("=")[1];
        console.log(hash)
        this.props.addToken(hash)
        let source = "spotifyToApple";
        this.props.addSource(source)
        let id = Math.floor(Math.random()*100000);
        this.props.addID(id)

    }

    render(){

        return(
            <Form>
            <div>
            <Form.Check
            type = "radio"
            id="male"
            name="gender"
            value="male"
            onClick={this.handleSelect1}
            label = "Apple Music to Spotify" />  
            <Form.Check 
            type = "radio"
            id="female"
            name="gender"
            value="female"
            onClick={this.handleSelect2}
            label = "Spotify to Apple Music" 
            />

            </div>
            </Form>

        )
    }
}



const mapDispatchToProps = (dispatch) => {
    return{
        addSource: (source)=> {dispatch({type: 'ADD_SOURCE', payload:source})}, 
        addID: (id) => {dispatch ({type: 'ADD_ID', payload:id})},
        addIDA2S: (idA2S) => {dispatch ({type: 'ADD_IDA2S', payload:idA2S })},
        addToken: (hash) => {dispatch ({type: 'ADD_SPOTIFYTOKEN', payload: hash})}
    }
 }




export default connect(null, mapDispatchToProps)(Source);
