import React, { Component } from 'react';

class Spotify extends Component {
    state = {
        url: null,
    }
    handleSubmit = async (e) => {
        e.preventDefault();
        var hash = document.URL
        console.log(hash)
        hash = hash.substring(5).split("&")[0].split("=")[1];
        console.log(hash)
        let playlistURI = this.state.url
        let token = async function(){
        fetch('http://localhost:8888/spotoken', {
        method: 'POST',
        headers: {
            'Content-type': 'application/json'
        },
        body: JSON.stringify({
            token: hash })
        })
        }
        let fillplaylist = async function(){
            await token().then(()=>{
                fetch('http://localhost:8888/spotify',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body: {
                    url: playlistURI
                }
                })
            })

        }
        fillplaylist();
        console.log(this.state); 
    }
   handleChange = async (e) => {
       this.setState({
           [e.target.id]: e.target.value
       })
   }
    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Spotify playlist URL</label>
            <input type = "text" id="url" onChange={this.handleChange}></input>
            <button>Submit</button>
            </form>

        </div>

        )
    }
}

export default Spotify;
