import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Form, Button, Col, Row } from 'react-bootstrap';

import './style.css';

class Converter extends Component{


    state = {
        url: " ",
        playName: null,
        link: null,
        done: null,
        valid: null
    }

    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
        console.log(this.state)

    }

    handleSubmit = (e) => { 


        e.preventDefault();




        const { source } = this.props;
        const theSource = { source };

        if ( theSource.source !== "spotifyToApple" && theSource.source !== "appleToSpotify"){
            this.setState({
                valid:"Please choose a source."
            })
        }

        if ( theSource.source === "appleToSpotify" ){

            const { appleToken } = this.props;
            const { idA2S }=this.props
            const theid = { idA2S }
            const { spotifyToken } = this.props;
            let playName = this.state.playName
            let playlistURL = this.state.url
            let playlistURI;


            if(playlistURL === " "){
                this.setState({
                    done:"Invalid ID"
                })
                console.log('why')
                return false;
            }
            this.setState({
                done:"Converting...",
                link: " ",
                valid: null
            })

            if (playlistURL.includes("us/playlist")){
                playlistURI = playlistURL.split("playlist/")[1]
                playlistURI=playlistURI.split("/")[1]

            }
            else if (playlistURL.includes("/playlist")){
                playlistURI=playlistURL.split("playlist/")[1]

            }
            else if (playlistURL.includes("p.")){

                playlistURI=playlistURL

            }
            else if (playlistURL.includes("pl.u")){
                playlistURI=playlistURL
            }else {
                this.setState({
                    done:"Invalid URL",
                    valid: null
                })
            }


            console.log(playlistURI)




            let getAppleISRC = async function(){
                fetch('http://localhost:8888/getAppleISRC',{
                    method: 'POST',
                    headers: {
                        'Content-type': 'application/json'
                },
                    body:JSON.stringify({
                        'url': playlistURI,
                        'authToken': { appleToken },
                        'id': theid
                })

                })/*.then((response)=>{
                    setTimeout(function(){
                        console.log(response.json())
                    },5000)
                })*/
            }

        let createSpotifyPlaylist =  (async()=>{
            fetch('http://localhost:8888/createSpotifyPlaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'createplaylist': playName,
                    'authToken': {spotifyToken},
                    'id':theid
                })
            }).then((response)=>{

                   return response.json()

            }).then((response)=>{

                    console.log(response)
                    console.log(response.done)
                    this.setState({
                        done:response.done,
                        link:response.link
                    })

            })
        })

        getAppleISRC().then(()=>{

            setTimeout(function(){
                createSpotifyPlaylist()

            },10000)
        })

        }
        if ( theSource.source ==="spotifyToApple"){

            const { appleToken } = this.props;
            const { spotifyToken } = this.props;
            const { id }=this.props
            const theid = { id }
            let playName = this.state.playName
            let playlistURL = this.state.url
            let playlistURI

            if(playlistURL === " "){
                this.setState({
                    done:"Invalid URL"
                })
                console.log('why')
                return false;
            }

            this.setState({
                done:"Converting...",
                link: " ",
                valid: null
            })

            if (playlistURL.includes("https://open")){

                playlistURI = playlistURL.split("?")[0]
                playlistURI=playlistURI.split("playlist/")[1]
            }
            else if (playlistURL.includes("open.spotify")){
                playlistURI = playlistURL.split("playlist/")[1]

            }
            else if (playlistURL.length === 22){
                playlistURI=playlistURL
            }else {
                this.setState({
                    done:"Invalid URL"
                })
            }


            console.log(playlistURI)


            let getSpotifyISRC = async function(){

                fetch('http://localhost:8888/getSpotifyISRC',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'url': playlistURI,
                    'authToken': { spotifyToken },
                    'id': theid
                })

                })    
            }


        let createApplePlaylist = (async()=>{
            fetch('http://localhost:8888/createApplePlaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'playlist': playName,
                    'authToken': { appleToken },
                    'id': theid

                })
            }).then((response)=>{

                return response.json()

             }).then((response)=>{

                 console.log(response)
                 console.log(response.done)
                 this.setState({
                     done:response.done,
                     link:response.link
                 })

            })
        })

        getSpotifyISRC().then(()=>{
            setTimeout(function(){
                createApplePlaylist()
            },10000)
        })

        }




    }


    render(){

        return(

        <Form>
        <Form.Group>
          <div className = "text-center">
          <Row className="justify-content-md-center">
          <Col xs={5} >
          <Form.Control  size = "med" type = "text" placeholder="Enter Playlist URL" onChange={this.handleChange} id="url" />
          </Col>
          </Row>
          <br />
          <Row className="justify-content-md-center">
          <Col xs={3}>
          <Form.Control  size = "med" type = "text" placeholder = "Enter Name of New Playlist" onChange={this.handleChange} id="playName"/>
          </Col>
          </Row>
          <br />
          <Button variant="primary" type="submit" onClick={this.handleSubmit} className="convert">Convert!</Button>
            <h1>{this.state.done}</h1>
            <a href = {this.state.link}>{this.state.link}</a>
            <h1>{this.state.valid}</h1>
            <p>"Note: If you are already logged into apple music, a popup may not appear"</p>
          </div>
        </Form.Group>
        </Form>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        appleToken: state.appleToken,
        id: state.id,
        spotifyToken: state.spotifyToken,
        idA2S: state.idA2S,
        source: state.source
    }
}

export default connect(mapStateToProps)(Converter);
