import React, { Component } from 'react';
import { connect } from 'react-redux'


class Source extends Component {

    //handleClick = async (e) => {






    //}

    render(){

        return(

        <div class="dropdown">
           <button onClick={this.handleClick} class="dropbtn"> Choose Playlist Source</button>
            <div id="dropdown" class="dropdown-content">
                <a>Apple Music to Spotify</a>
                <a>Spotify to Apple Music</a>

            </div>
        </div>

        )
    }
}



const mapDispatchToProps = (dispatch) => {
    return{
        addSource: (source)=> {dispatch({type: 'ADD_SOURCE'})}
    }
}


export default connect(null, mapDispatchToProps)(Source);
