import React, { Component } from 'react';
import { connect } from 'react-redux'
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';


class Source extends Component {

    handleSelect = (e) =>{
        console.log(e)

    }

    render(){

        return(
            <div>
            <DropdownButton 
            id="dropdown-basic-button" 
            title="Dropdown button" 
            onSelect={this.handleSelect}>
            <Dropdown.Item href="#/action-1">Apple Music to Spotify</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Spotify to Apple Music</Dropdown.Item>
            </DropdownButton>

            <button>Convert!</button>
            </div>

        )
    }
}



const mapDispatchToProps = (dispatch) => {
    return{
        addSource: (source)=> {dispatch({type: 'ADD_SOURCE'})}
    }
}


export default connect(null, mapDispatchToProps)(Source);
