import React, { Component } from 'react';

class createaPlaylist extends Component{
    state = {
        url: null,
    }
    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = async (e) => {
        var tag = this.state.url
        fetch('http://localhost:8888/createplaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'createplaylist': tag 
                })
        })
    }
    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Name of your new playlist</label>
            <input type = "text" id="url" onChange={this.handleChange}></input>
            <button>Submit</button>
            </form>

        </div>

        )
    }
}

export default createaPlaylist;
