import React, { Component } from 'react';
import { connect } from 'react-redux'



class convertApple extends Component {
    state = {
        url: null,
        authtoken: null
    }
    handleSubmit = async (e) => {
        e.preventDefault();
        let playlistURI = this.state.url
        const { appleToken } = this.props;
        const Token = { appleToken };
        console.log(Token)
        const { idA2S }=this.props
        const theid = { idA2S }
        let getAppleISRC = async function(){
            setTimeout (function(){

                    fetch('http://localhost:8888/getAppleISRC',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'url': playlistURI,
                    'authToken': Token,
                    'id': theid
                })

                })

            },5000)  
        }

        getAppleISRC();
        console.log(this.state); 
    }
   handleChange = async (e) => {
       this.setState({
           [e.target.id]: e.target.value
       })
   }
    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Apple playlist URL to Convert</label>
            <input type = "text" id="url" onChange={this.handleChange}></input>
            <button>Submit</button>
            </form>

        </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        appleToken: state.appleToken,
        idA2S: state.id
    }
}



export default connect(mapStateToProps)(Spotify);
