import React, { Component } from 'react';
import { connect } from 'react-redux'

class CreateApple extends Component{

   state = {
        url: null,
        playName: null,
    }
    handleChange = async (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = async (e) => {
        const { appleToken } = this.props;
        const Token = { appleToken };
        var tag = this.state.url
        fetch('http://localhost:8888/createplaylist',{
                method: 'POST',
                headers: {
                    'Content-type': 'application/json'
                },
                body:JSON.stringify({
                    'createplaylist': tag,
                    'authToken': Token
                })
        })
    }

    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>Enter Name of your new playlist</label>
            <input type = "text" id="playName" onChange={this.handleChange}></input>
            <button>Submit</button>
            <h1>{this.state.url}</h1>
            </form>

        </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        appleToken: state.appleToken
    }
}

export default connect(mapStateToProps)(CreateApple);
