import React, { Component } from 'react';
import { connect } from 'react-redux'



class Source extends Component {

    handleSelect = (e) =>{
        console.log("coconut")

    }

    render(){

        return(
            <div>
            <input type="radio" id="male" name="gender" value="male" onClick={this.handleSelect}></input>
            <label for="male" >Apple Music to Spotify</label>
            <input type="radio" id="male" name="gender" value="female" onClick={this.handleSelect}></input>
            <label for="female">Spotify to Apple Music</label>

            <button>Convert!</button>
            </div>

        )
    }
}



const mapDispatchToProps = (dispatch) => {
    return{
        addSource: (source)=> {dispatch({type: 'ADD_SOURCE'})}
    }
}


export default connect(null, mapDispatchToProps)(Source);
